import random 

from os import system

import time



class secretBakra:
   
    def __init__(self,names,state = None):
   
        self.allnames = names
        self.peps_done = []
        self.state = state
       
        if self.state : 
            random.setstate(self.state)
            # print(type(self.state)) 
        else:
            self.state = random.getstate()
            
        
        self.rand_allnames = self.rand()

    def rand(self):
        ran = dict()
        done = []
        names = [i for i in self.allnames if i not in done]
        
        for i in range(len(self.allnames)):
            
            if self.allnames[i] in names:
                names.remove(self.allnames[i])
            ran[self.allnames[i]] = random.choice(names)
            done.append(ran[self.allnames[i]])
            
            names = [i for i in self.allnames if i not in done]
        
        return ran
    

    def check(self):
        for key,value in self.rand_allnames.items():
            if key == value:
                raise Exception("Error : same names in the dictionary")
        return True

    def __call__(self):
        
        
        # print(f"{self.rand_allnames}") # for checking the output
        print(f"{self.state}")

        return self.rand_allnames
    
    def write(self):
        f = open("names2.txt", "w")
        f.write(f" format ( name : ur_secret_bakra )  \n{self.rand_allnames}")
        f.close()
         
         

